    private void btn_adduserActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_adduserActionPerformed
       
        MenuPrincipal entrymenu = new MenuPrincipal();
        Create createuser = new Create();
        NoRepeat norepeat = new NoRepeat();
        
        if(txt_nome.getText().length()>0 && new String (psw_pw.getPassword()).length()>0){
            createuser.nome = txt_nome.getText();
            createuser.password = new String(psw_pw.getPassword());
        }else
            JOptionPane.showMessageDialog(null, "Insira um utilizador e ou uma password");
        
        
        norepeat.nome = txt_nome.getText();
        if(norepeat.verifyRepetedUseres()){
            JOptionPane.showMessageDialog(null,"O utilizador j√° existe! Escolha outro nome de utilizador");
            txt_nome.setText(null);
        }else{
            
            if(createuser.CreateNewUser()==true)
            {
            entrymenu.userLogin = txt_nome.getText();
            this.dispose();
            }
        }
        
    }//GEN-LAST:event_btn_adduserActionPerformed

